        public static DateTime DailyLastStart
        {
            get
            {
                var key = Registry.LocalMachine.CreateSubKey("Software\\ExtraClub\\");

                if(key != null)
                {
                    var res = DateTime.Parse(key.GetValue("DailyLastStartExtraClub", DateTime.MinValue).ToString());
                    try
                    {
                        if(res == DateTime.MinValue)
                        {
                            key.SetValue("DailyLastStartExtraClub", DateTime.Today.AddDays(-1));
                        }
                    }
                    catch(Exception ex)
                    {
                        Logger.Log("Ошибка при назначении даты в реестре");
                        Logger.Log(ex);
                    }
                    return res;
                }
                throw new NullReferenceException("Software\\ExtraClub is null");
            }
            set
            {
                Logger.Log("Установка даты в реестре");
                Logger.Log(new StackTrace());
                var key = Registry.LocalMachine.CreateSubKey("Software\\ExtraClub\\");
                if(key != null)
                {
                    key.SetValue("DailyLastStartExtraClub", value);
                }
            }
        }

