                            ProcessDivisionTasks(context, divs);
                            if (e.Cancel) return;
                            Log.WriteLine("Запуск ежедневных задач - 7");
                            ProcessCutomerBirthdays(context);
                            if (e.Cancel) return;
                            Log.WriteLine("Запуск ежедневных задач - 8");
